  (1, '2016-04-18 09:10:29', '0000-00-00 00:00:00', 'Drop', 'Drop', 1, 0, 1, 0, 0, 0, '', 'js/drop.min.js', 'css/drop-theme-arrows-bounce.min.css', '0', '', ''),
  (2, '2016-04-18 09:10:29', '0000-00-00 00:00:00', 'EmbeddedJS', 'Embedded JS', 1, 0, 3, 0, 0, 0, '', 'js/ejs_production.js, js/ejs_viewhelpers.js', '', '0', '', ''),
  (3, '2016-04-18 09:10:29', '0000-00-00 00:00:00', 'FontAwesome', 'Font Awesome', 4, 5, 1, 0, 0, 0, '', '', 'h5p-font-awesome.min.css', '0', '', ''),
  (4, '2016-04-18 09:10:29', '0000-00-00 00:00:00', 'H5P.Accordion', 'Accordion', 1, 0, 5, 1, 0, 0, 'iframe', 'h5p-accordion.js', 'h5p-accordion.css', '0', '[\n  {\n    "name": "panels",\n    "type": "list",\n    "label": "Panels",\n    "entity": "panel",\n    "max": 100,\n    "min": 1,\n    "field": {\n      "name": "content",\n      "type": "group",\n      "label": "Content",\n      "entity": "content",\n      "fields": [\n        {\n          "name": "title",\n          "type": "text",\n          "label": "Title"\n        },\n        {\n          "name": "content",\n          "type": "library",\n          "label": "Content type",\n          "entity": "content",\n          "options": [\n            "H5P.AdvancedText 1.1"\n          ]\n        }\n      ]\n    }\n  },\n  {\n    "name": "hTag",\n    "type": "select",\n    "label": "H tags for labels (does not affect the size of the label)",\n    "description": "The h tag used on the labels. Normally H2 but if this belongs under an H2 heading use H3. Does not affect the size of the labels, only used for semantical purposes.",\n    "options": [\n      {\n        "value": "h2",\n        "label": "H2"\n      },\n      {\n        "value": "h3",\n        "label": "H3"\n      },\n      {\n        "value": "h4",\n        "label": "H4"\n      }\n    ],\n    "default": "h2"\n  }\n]\n', ''),
  (5, '2016-04-18 09:10:29', '0000-00-00 00:00:00', 'H5P.AdvancedText', 'Advanced Text', 1, 1, 0, 0, 0, 0, '', 'text.js', 'text.css', '0', '[\n  {\n    "name": "text",\n    "type": "text",\n    "widget": "html",\n    "label": "Text",\n    "enterMode": "p",\n    "tags": [\n      "strong",\n      "em",\n      "del",\n      "a",\n      "ul",\n      "ol",\n      "h2",\n      "h3",\n      "hr"\n    ],\n    "font": {\n      "size": true,\n      "color": true,\n      "background": true\n    }\n  }\n]\n', ''),
  (6, '2016-04-18 09:10:29', '0000-00-00 00:00:00', 'H5P.AppearIn', 'appear.in for Chat and Talk', 1, 0, 3, 1, 0, 0, '', 'h5p-appear-in.js', '', '0', '[\n  {\n    "label": "Room name",\n    "name": "appearRoom",\n    "type": "text",\n    "default": "h5p",\n    "description": "Name of the appear.in room"\n  },\n  {\n    "label": "Fit to container",\n    "name": "fitToContainer",\n    "type": "boolean",\n    "default": true,\n    "description": "Make the room fit its container, will override any height and width defined with 100% values."\n  },\n  {\n    "label": "Height",\n    "name": "appearHeight",\n    "type": "number",\n    "default": "400",\n    "description": "Set Height of chat/talk room"\n  },\n  {\n    "label": "Width",\n    "name": "appearWidth",\n    "type": "number",\n    "default": "550",\n    "description": "Set Width of chat/talk room"\n  }\n]', ''),
  (7, '2016-04-18 09:10:29', '0000-00-00 00:00:00', 'H5P.Audio', 'Audio', 1, 2, 2, 1, 0, 0, '', 'scripts/audio.js', 'styles/audio.css', '0', '[\n  {\n    "name": "files",\n    "type": "audio",\n    "label": "Source files"\n  },\n  {\n    "name": "playerMode",\n    "type": "select",\n    "label": "Player mode",\n    "description": "Select the layout of the player.",\n    "options": [\n      {\n        "value": "minimalistic",\n        "label": "Minimalistic"\n      },\n      {\n        "value": "full",\n        "label": "Full"\n      }\n    ],\n    "default": "minimalistic"\n  },\n  {\n    "name": "fitToWrapper",\n    "type": "boolean",\n    "label": "Fit to wrapper",\n    "default": false,\n    "descriptions": "Adjust audioplayer size to use available space"\n  },\n  {\n    "name": "controls",\n    "type": "boolean",\n    "label": "Enable controls",\n    "default": true,\n    "description": "Controls allow the user to for instance pause the audio"\n  },\n  {\n    "name": "autoplay",\n    "type": "boolean",\n    "label": "Enable autoplay",\n    "description": "With autoplay the audio starts to play immediately. If autoplay is disabled the user presses a play button to start the audio."\n  },\n  {\n    "name": "copyright",\n    "type": "group",\n    "label": "Copyright information",\n    "deprecated": true,\n    "optional": true,\n    "fields": [\n      {\n        "name": "title",\n        "type": "text",\n        "label": "Title",\n        "placeholder": "Für Elise",\n        "optional": true\n      },\n      {\n        "name": "author",\n        "type": "text",\n        "label": "Author",\n        "placeholder": "Ludwig van Beethoven",\n        "optional": true\n      },\n      {\n        "name": "source",\n        "type": "text",\n        "label": "Source",\n        "placeholder": "http://en.wikipedia.org/wiki/F%C3%BCr_Elise",\n        "optional": true,\n        "regexp": {\n          "pattern": "^http[s]?://.+",\n          "modifiers": "i"\n        }\n      },\n  